import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import moment from 'moment-timezone';
import Confetti from 'react-confetti';
import BirthdayAnimations from './BirthdayAnimations';

const bounce = keyframes`
  0%, 20%, 50%, 80%, 100% { transform: translateY(0); }
  40% { transform: translateY(-20px); }
  60% { transform: translateY(-10px); }
`;

const rainbow = keyframes`
  0% { color: #ff6b6b; }
  16% { color: #4ecdc4; }
  32% { color: #45b7d1; }
  48% { color: #96ceb4; }
  64% { color: #ffeaa7; }
  80% { color: #dda0dd; }
  100% { color: #ff6b6b; }
`;

const CountdownContainer = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background: linear-gradient(135deg, #ff9a9e, #fecfef, #fecfef);
  color: #333;
  text-align: center;
  padding: 20px;
  position: relative;
  overflow: hidden;
`;

const Title = styled.h1`
  font-family: 'Dancing Script', cursive;
  font-size: clamp(2.5rem, 8vw, 4.5rem);
  font-weight: 700;
  margin-bottom: 1rem;
  animation: ${rainbow} 3s ease-in-out infinite;
`;

const SubTitle = styled.p`
  font-size: clamp(1.2rem, 4vw, 1.8rem);
  margin-bottom: 2rem;
  color: #666;
`;

const CountdownGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 1rem;
  margin-bottom: 3rem;

  @media (max-width: 768px) {
    grid-template-columns: repeat(2, 1fr);
    gap: 0.5rem;
  }
`;

const TimeUnit = styled(motion.div)`
  background: rgba(255, 255, 255, 0.9);
  backdrop-filter: blur(10px);
  border-radius: 20px;
  padding: 1.5rem 1rem;
  box-shadow: 0 8px 32px rgba(255, 182, 193, 0.3);
  border: 2px solid rgba(255, 182, 193, 0.2);

  @media (max-width: 768px) {
    padding: 1rem 0.5rem;
  }
`;

const TimeNumber = styled.div`
  font-size: clamp(2rem, 6vw, 3rem);
  font-weight: 700;
  color: #ff6b6b;
  animation: ${bounce} 2s ease-in-out infinite;
`;

const TimeLabel = styled.div`
  font-size: clamp(0.8rem, 3vw, 1rem);
  color: #666;
  margin-top: 0.5rem;
`;

const Message = styled(motion.div)`
  max-width: 600px;
  font-size: clamp(1rem, 4vw, 1.3rem);
  line-height: 1.6;
  margin-bottom: 2rem;
  background: rgba(255, 255, 255, 0.9);
  backdrop-filter: blur(10px);
  padding: 2rem;
  border-radius: 25px;
  box-shadow: 0 8px 32px rgba(255, 182, 193, 0.3);
  border: 2px solid rgba(255, 182, 193, 0.2);
  color: #555;
`;

const SorryNote = styled(motion.div)`
  max-width: 500px;
  font-size: clamp(1rem, 4vw, 1.2rem);
  line-height: 1.6;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  padding: 1.5rem;
  border-radius: 20px;
  box-shadow: 0 8px 32px rgba(255, 182, 193, 0.4);
  border: 2px solid rgba(255, 182, 193, 0.3);
  color: #666;
  font-style: italic;
`;

const CelebrationContainer = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background: linear-gradient(135deg, #ff9a9e, #fad0c4, #ffd1ff);
  z-index: 1000;
`;

const CelebrationText = styled.h1`
  font-family: 'Dancing Script', cursive;
  font-size: clamp(3rem, 10vw, 6rem);
  font-weight: 700;
  color: #ff6b6b;
  text-align: center;
  margin-bottom: 2rem;
  animation: ${rainbow} 2s ease-in-out infinite;
`;

function BirthdayCountdown({ onComplete }) {
  const [timeLeft, setTimeLeft] = useState({});
  const [isComplete, setIsComplete] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => {
      const now = moment.tz('Asia/Karachi');
      const birthday = moment.tz('2025-01-04 00:00:00', 'Asia/Karachi');
      const diff = birthday.diff(now);

      if (diff > 0) {
        const duration = moment.duration(diff);
        setTimeLeft({
          days: Math.floor(duration.asDays()),
          hours: duration.hours(),
          minutes: duration.minutes(),
          seconds: duration.seconds()
        });
      } else {
        setIsComplete(true);
        setShowConfetti(true);
        clearInterval(timer);
        onComplete();
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [onComplete]);

  if (isComplete) {
    return (
      <AnimatePresence>
        <CelebrationContainer
          initial={{ opacity: 0, scale: 0.5 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1 }}
        >
          {showConfetti && <Confetti />}
          <BirthdayAnimations />
          <CelebrationText>
            Happy Birthday Aiman! ðŸŽ‚
          </CelebrationText>
          <motion.p
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 1, duration: 1 }}
            style={{ 
              fontSize: 'clamp(1.2rem, 5vw, 2rem)', 
              textAlign: 'center', 
              maxWidth: '600px', 
              padding: '0 20px',
              color: '#666'
            }}
          >
            Today is all about celebrating the most amazing person I know! 
            You make every day brighter just by being you! ðŸŒŸðŸ’–
          </motion.p>
        </CelebrationContainer>
      </AnimatePresence>
    );
  }

  return (
    <CountdownContainer>
      <motion.div
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        <Title>Birthday Countdown! ðŸŽ‰</Title>
        <SubTitle>Aiman's Special Day is Coming!</SubTitle>
      </motion.div>

      <CountdownGrid>
        <TimeUnit
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.2, duration: 0.5 }}
        >
          <TimeNumber>{timeLeft.days || 0}</TimeNumber>
          <TimeLabel>Days</TimeLabel>
        </TimeUnit>
        <TimeUnit
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.4, duration: 0.5 }}
        >
          <TimeNumber>{timeLeft.hours || 0}</TimeNumber>
          <TimeLabel>Hours</TimeLabel>
        </TimeUnit>
        <TimeUnit
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.6, duration: 0.5 }}
        >
          <TimeNumber>{timeLeft.minutes || 0}</TimeNumber>
          <TimeLabel>Minutes</TimeLabel>
        </TimeUnit>
        <TimeUnit
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.8, duration: 0.5 }}
        >
          <TimeNumber>{timeLeft.seconds || 0}</TimeNumber>
          <TimeLabel>Seconds</TimeLabel>
        </TimeUnit>
      </CountdownGrid>

      <Message
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 1, duration: 1 }}
      >
        My beautiful Aiman, your birthday is almost here! I can't wait to celebrate 
        the incredible person you are. You bring so much joy, laughter, and love into 
        this world. Every day with you feels like a gift, and your birthday is the 
        perfect time to remind you how special you are to me! ðŸŽ‚âœ¨
      </Message>

      <SorryNote
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 1.5, duration: 1 }}
      >
        I'm so sorry I can't be there with you on your special day, my love. 
        Distance may keep us apart physically, but my heart is always with you. 
        I wish I could give you the biggest hug and celebrate together, but until then, 
        know that you're in my thoughts every moment. ðŸ’•
      </SorryNote>
    </CountdownContainer>
  );
}

export default BirthdayCountdown;
