import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import moment from 'moment-timezone';
import NewYearPage from './components/NewYearPage';
import TestMode from './components/TestMode';

const AppContainer = styled.div`
  min-height: 100vh;
  position: relative;
  overflow-x: hidden;
`;

const TestModeToggle = styled.button`
  position: fixed;
  top: 20px;
  right: 20px;
  z-index: 2000;
  background: rgba(0, 0, 0, 0.8);
  color: white;
  border: none;
  padding: 15px 20px;
  border-radius: 25px;
  font-size: 0.9rem;
  cursor: pointer;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  transition: all 0.3s ease;

  &:hover {
    background: rgba(0, 0, 0, 0.9);
    transform: translateY(-2px);
  }
`;

function App() {
  const [currentPhase, setCurrentPhase] = useState('newYear');
  const [isTestMode, setIsTestMode] = useState(false);

  useEffect(() => {
    const checkPhase = () => {
      const now = moment.tz('Asia/Karachi');
      const newYear2025 = moment.tz('2025-01-01 00:00:00', 'Asia/Karachi');

      console.log('Current time (Pakistan):', now.format('YYYY-MM-DD HH:mm:ss'));
      console.log('New Year 2025:', newYear2025.format('YYYY-MM-DD HH:mm:ss'));

      // For now, always show New Year countdown
      // Birthday functionality is hidden until after New Year
      setCurrentPhase('newYear');
    };

    if (!isTestMode) {
      checkPhase();
      const interval = setInterval(checkPhase, 10000);
      return () => clearInterval(interval);
    }
  }, [isTestMode]);

  const handleNewYearComplete = () => {
    // After New Year celebration, we can reveal birthday content
    // For now, just stay on New Year
    console.log('New Year celebration complete!');
  };

  if (isTestMode) {
    return (
      <AppContainer>
        <TestMode onClose={() => setIsTestMode(false)} />
      </AppContainer>
    );
  }

  return (
    <AppContainer>
      <TestModeToggle onClick={() => setIsTestMode(true)}>
        ðŸ§ª Test Mode
      </TestModeToggle>

      <AnimatePresence mode="wait">
        <NewYearPage key="newYear" onComplete={handleNewYearComplete} />
      </AnimatePresence>
    </AppContainer>
  );
}

export default App;
