import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import moment from 'moment-timezone';
import NewYearPage from './components/NewYearPage';
import BirthdayPage from './components/BirthdayPage';

const AppContainer = styled.div`
  min-height: 100vh;
  position: relative;
  overflow-x: hidden;
`;

function App() {
  const [currentPhase, setCurrentPhase] = useState('newYear');

  useEffect(() => {
    const checkPhase = () => {
      const now = moment.tz('Asia/Karachi');
      const newYear2025 = moment.tz('2025-01-01 00:00:00', 'Asia/Karachi');
      const birthday2025 = moment.tz('2025-01-04 00:00:00', 'Asia/Karachi');

      if (now.isBefore(newYear2025)) {
        setCurrentPhase('newYear');
      } else if (now.isBefore(birthday2025)) {
        setCurrentPhase('waitingForBirthday');
      } else {
        setCurrentPhase('birthday');
      }
    };

    checkPhase();
    const interval = setInterval(checkPhase, 60000); // Check every minute

    return () => clearInterval(interval);
  }, []);

  const handleNewYearComplete = () => {
    setCurrentPhase('waitingForBirthday');
  };

  return (
    <AppContainer>
      <AnimatePresence mode="wait">
        {currentPhase === 'newYear' && (
          <NewYearPage key="newYear" onComplete={handleNewYearComplete} />
        )}
        {(currentPhase === 'waitingForBirthday' || currentPhase === 'birthday') && (
          <BirthdayPage key="birthday" phase={currentPhase} />
        )}
      </AnimatePresence>
    </AppContainer>
  );
}

export default App;
