import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import moment from 'moment-timezone';
import NewYearCountdown from './components/NewYearCountdown';
import BirthdayCountdown from './components/BirthdayCountdown';
import Gallery from './components/Gallery';
import LoadingScreen from './components/LoadingScreen';

const AppContainer = styled.div`
  min-height: 100vh;
  position: relative;
  overflow-x: hidden;
`;

function App() {
  const [currentView, setCurrentView] = useState('loading');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Loading screen for 3 seconds
    const timer = setTimeout(() => {
      setIsLoading(false);
      determineCurrentView();
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  const determineCurrentView = () => {
    const now = moment.tz('Asia/Karachi');
    const newYear2025 = moment.tz('2025-01-01 00:00:00', 'Asia/Karachi');
    const birthday2025 = moment.tz('2025-01-04 00:00:00', 'Asia/Karachi');

    if (now.isBefore(newYear2025)) {
      setCurrentView('newYear');
    } else if (now.isBefore(birthday2025)) {
      setCurrentView('birthday');
    } else {
      setCurrentView('gallery');
    }
  };

  const handleNewYearComplete = () => {
    setTimeout(() => {
      setCurrentView('birthday');
    }, 10000); // Show celebration for 10 seconds
  };

  const handleBirthdayComplete = () => {
    setTimeout(() => {
      setCurrentView('gallery');
    }, 10000); // Show celebration for 10 seconds
  };

  if (isLoading) {
    return <LoadingScreen />;
  }

  return (
    <AppContainer>
      <AnimatePresence mode="wait">
        {currentView === 'newYear' && (
          <NewYearCountdown key="newYear" onComplete={handleNewYearComplete} />
        )}
        {currentView === 'birthday' && (
          <BirthdayCountdown key="birthday" onComplete={handleBirthdayComplete} />
        )}
        {currentView === 'gallery' && (
          <Gallery key="gallery" />
        )}
      </AnimatePresence>
    </AppContainer>
  );
}

export default App;
