import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import moment from 'moment-timezone';
import Confetti from 'react-confetti';

const glow = keyframes`
  0% { text-shadow: 0 0 20px rgba(255, 255, 255, 0.5); }
  50% { text-shadow: 0 0 30px rgba(255, 255, 255, 0.8), 0 0 40px rgba(255, 255, 255, 0.6); }
  100% { text-shadow: 0 0 20px rgba(255, 255, 255, 0.5); }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

const sparkle = keyframes`
  0%, 100% { opacity: 0; transform: scale(0); }
  50% { opacity: 1; transform: scale(1); }
`;

const PageContainer = styled.div`
  min-height: 100vh;
  background: #000;
  color: white;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 20px;
  position: relative;
  overflow: hidden;
`;

const BackgroundElements = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  pointer-events: none;
`;

const Sparkle = styled.div`
  position: absolute;
  width: 3px;
  height: 3px;
  background: white;
  border-radius: 50%;
  animation: ${sparkle} 3s ease-in-out infinite;
  animation-delay: ${props => props.delay}s;
  left: ${props => props.left}%;
  top: ${props => props.top}%;
`;

const MainTitle = styled(motion.h1)`
  font-family: 'Dancing Script', cursive;
  font-size: clamp(2.5rem, 8vw, 4.5rem);
  font-weight: 700;
  text-align: center;
  margin-bottom: 1rem;
  animation: ${glow} 3s ease-in-out infinite;
`;

const SubTitle = styled(motion.p)`
  font-size: clamp(1rem, 3vw, 1.3rem);
  text-align: center;
  margin-bottom: 3rem;
  opacity: 0.8;
  font-weight: 300;
`;

const CountdownContainer = styled(motion.div)`
  display: flex;
  gap: 2rem;
  margin-bottom: 2rem;

  @media (max-width: 768px) {
    gap: 1rem;
    flex-wrap: wrap;
    justify-content: center;
  }
`;

const TimeUnit = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: center;
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 15px;
  padding: 1.5rem 1rem;
  min-width: 100px;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: -1px;
    left: -1px;
    right: -1px;
    bottom: -1px;
    background: linear-gradient(45deg, transparent, rgba(255, 255, 255, 0.1), transparent);
    border-radius: 15px;
    z-index: -1;
  }

  @media (max-width: 768px) {
    min-width: 80px;
    padding: 1rem 0.5rem;
  }
`;

const TimeNumber = styled.div`
  font-size: clamp(2.5rem, 8vw, 4rem);
  font-weight: 700;
  line-height: 1;
  color: #fff;
  animation: ${pulse} 2s ease-in-out infinite;
`;

const TimeLabel = styled.div`
  font-size: clamp(0.8rem, 2vw, 1rem);
  margin-top: 0.5rem;
  opacity: 0.7;
  text-transform: uppercase;
  letter-spacing: 2px;
  font-weight: 500;
`;

const PersonalMessage = styled(motion.div)`
  max-width: 600px;
  text-align: center;
  font-size: clamp(1rem, 3vw, 1.2rem);
  line-height: 1.6;
  opacity: 0.9;
  font-weight: 300;
  background: rgba(255, 255, 255, 0.03);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  padding: 2rem;
`;

const CelebrationOverlay = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(45deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4, #ffeaa7);
  background-size: 400% 400%;
  animation: gradientShift 4s ease infinite;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  z-index: 1000;

  @keyframes gradientShift {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }
`;

const CelebrationText = styled(motion.h1)`
  font-family: 'Dancing Script', cursive;
  font-size: clamp(3rem, 12vw, 7rem);
  font-weight: 700;
  color: white;
  text-align: center;
  margin-bottom: 1rem;
  text-shadow: 3px 3px 6px rgba(0,0,0,0.3);
`;

const CelebrationSubtext = styled(motion.p)`
  font-size: clamp(1.2rem, 5vw, 2.5rem);
  color: white;
  text-align: center;
  max-width: 700px;
  padding: 0 20px;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
  font-weight: 600;
`;

const Firework = styled(motion.div)`
  position: absolute;
  width: 6px;
  height: 6px;
  background: ${props => props.color};
  border-radius: 50%;

  &::before {
    content: '';
    position: absolute;
    top: -25px;
    left: -25px;
    width: 50px;
    height: 50px;
    border: 3px solid ${props => props.color};
    border-radius: 50%;
    opacity: 0;
    animation: explode 1.5s ease-out forwards;
  }

  &::after {
    content: '';
    position: absolute;
    top: -15px;
    left: -15px;
    width: 30px;
    height: 30px;
    background: radial-gradient(circle, ${props => props.color} 0%, transparent 70%);
    border-radius: 50%;
    opacity: 0;
    animation: explode 2s ease-out forwards;
  }

  @keyframes explode {
    0% { transform: scale(0); opacity: 1; }
    50% { transform: scale(1); opacity: 0.8; }
    100% { transform: scale(2); opacity: 0; }
  }
`;

function NewYearPage({ onComplete }) {
  const [timeLeft, setTimeLeft] = useState({});
  const [isComplete, setIsComplete] = useState(false);
  const [showCelebration, setShowCelebration] = useState(false);
  const [fireworks, setFireworks] = useState([]);
  const [sparkles, setSparkles] = useState([]);

  useEffect(() => {
    // Create sparkles for background
    const newSparkles = Array.from({ length: 30 }, (_, i) => ({
      id: i,
      left: Math.random() * 100,
      top: Math.random() * 100,
      delay: Math.random() * 3
    }));
    setSparkles(newSparkles);
  }, []);

  useEffect(() => {
    const timer = setInterval(() => {
      const now = moment.tz('Asia/Karachi');
      const newYear = moment.tz('2025-01-01 00:00:00', 'Asia/Karachi');
      const diff = newYear.diff(now);

      console.log('New Year countdown - Current time:', now.format('YYYY-MM-DD HH:mm:ss'));
      console.log('New Year countdown - Target time:', newYear.format('YYYY-MM-DD HH:mm:ss'));
      console.log('New Year countdown - Diff:', diff);

      if (diff > 0) {
        const duration = moment.duration(diff);
        setTimeLeft({
          days: Math.floor(duration.asDays()),
          hours: duration.hours(),
          minutes: duration.minutes(),
          seconds: duration.seconds()
        });
      } else {
        setIsComplete(true);
        setShowCelebration(true);
        clearInterval(timer);

        // Create fireworks
        const newFireworks = Array.from({ length: 20 }, (_, i) => ({
          id: i,
          x: Math.random() * window.innerWidth,
          y: Math.random() * window.innerHeight,
          color: ['#ff6b6b', '#4ecdc4', '#45b7d1', '#96ceb4', '#ffeaa7', '#dda0dd'][Math.floor(Math.random() * 6)],
          delay: Math.random() * 3
        }));
        setFireworks(newFireworks);

        setTimeout(() => {
          onComplete();
        }, 8000);
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [onComplete]);

  if (showCelebration) {
    return (
      <AnimatePresence>
        <CelebrationOverlay
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <Confetti 
            width={window.innerWidth}
            height={window.innerHeight}
            numberOfPieces={300}
            recycle={false}
            gravity={0.3}
          />

          {fireworks.map(firework => (
            <Firework
              key={firework.id}
              color={firework.color}
              style={{ left: firework.x, top: firework.y }}
              initial={{ scale: 0, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ delay: firework.delay, duration: 0.5 }}
            />
          ))}

          <CelebrationText
            initial={{ scale: 0, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ delay: 1, duration: 1.5, type: "spring", stiffness: 100 }}
          >
            ðŸŽ‰ Happy New Year 2025! ðŸŽ‰
          </CelebrationText>

          <CelebrationSubtext
            initial={{ y: 100, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 2.5, duration: 1 }}
          >
            Welcome to 2025, beautiful Aiman! âœ¨
          </CelebrationSubtext>
        </CelebrationOverlay>
      </AnimatePresence>
    );
  }

  return (
    <PageContainer>
      <BackgroundElements>
        {sparkles.map(sparkle => (
          <Sparkle
            key={sparkle.id}
            left={sparkle.left}
            top={sparkle.top}
            delay={sparkle.delay}
          />
        ))}
      </BackgroundElements>

      <MainTitle
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1.5, ease: "easeOut" }}
      >
        New Year 2025
      </MainTitle>

      <SubTitle
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.5, duration: 1 }}
      >
        Counting down to something magical...
      </SubTitle>

      <CountdownContainer
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ delay: 1, duration: 1, ease: "easeOut" }}
      >
        <TimeUnit
          whileHover={{ scale: 1.05 }}
          transition={{ type: "spring", stiffness: 300 }}
        >
          <TimeNumber>{timeLeft.days || 0}</TimeNumber>
          <TimeLabel>Days</TimeLabel>
        </TimeUnit>
        <TimeUnit
          whileHover={{ scale: 1.05 }}
          transition={{ type: "spring", stiffness: 300 }}
        >
          <TimeNumber>{timeLeft.hours || 0}</TimeNumber>
          <TimeLabel>Hours</TimeLabel>
        </TimeUnit>
        <TimeUnit
          whileHover={{ scale: 1.05 }}
          transition={{ type: "spring", stiffness: 300 }}
        >
          <TimeNumber>{timeLeft.minutes || 0}</TimeNumber>
          <TimeLabel>Minutes</TimeLabel>
        </TimeUnit>
        <TimeUnit
          whileHover={{ scale: 1.05 }}
          transition={{ type: "spring", stiffness: 300 }}
        >
          <TimeNumber>{timeLeft.seconds || 0}</TimeNumber>
          <TimeLabel>Seconds</TimeLabel>
        </TimeUnit>
      </CountdownContainer>

      <PersonalMessage
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 1.5, duration: 1 }}
      >
        Hey Aiman! 2025 is almost here and I can't wait to start this new year thinking about you. 
        Every second that passes brings us closer to new memories, new moments, and new reasons to smile. 
        You make everything brighter! âœ¨
      </PersonalMessage>
    </PageContainer>
  );
}

export default NewYearPage;
