import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import moment from 'moment-timezone';
import Confetti from 'react-confetti';

const PageContainer = styled.div`
  min-height: 100vh;
  background: #0a0a0a;
  color: white;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 20px;
  position: relative;
`;

const SimpleTitle = styled(motion.h1)`
  font-family: 'Dancing Script', cursive;
  font-size: clamp(2rem, 6vw, 3.5rem);
  font-weight: 400;
  text-align: center;
  margin-bottom: 2rem;
  color: #f8f8f8;
`;

const CountdownContainer = styled(motion.div)`
  display: flex;
  gap: 2rem;
  margin-bottom: 2rem;

  @media (max-width: 768px) {
    gap: 1rem;
    flex-wrap: wrap;
    justify-content: center;
  }
`;

const TimeBox = styled.div`
  text-align: center;
  min-width: 80px;
`;

const TimeNumber = styled.div`
  font-size: clamp(2.5rem, 8vw, 4rem);
  font-weight: 300;
  color: #fff;
  line-height: 1;
`;

const TimeLabel = styled.div`
  font-size: 0.9rem;
  color: #ccc;
  margin-top: 0.5rem;
  text-transform: uppercase;
  letter-spacing: 1px;
`;

const SimpleMessage = styled(motion.p)`
  font-size: clamp(1rem, 3vw, 1.2rem);
  text-align: center;
  max-width: 500px;
  line-height: 1.6;
  color: #ddd;
  font-weight: 300;
`;

const CelebrationOverlay = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(45deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4);
  background-size: 400% 400%;
  animation: gradientShift 3s ease infinite;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  z-index: 1000;

  @keyframes gradientShift {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }
`;

const CelebrationText = styled(motion.h1)`
  font-family: 'Dancing Script', cursive;
  font-size: clamp(3rem, 10vw, 6rem);
  font-weight: 700;
  color: white;
  text-align: center;
  margin-bottom: 1rem;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
`;

const CelebrationSubtext = styled(motion.p)`
  font-size: clamp(1.2rem, 4vw, 2rem);
  color: white;
  text-align: center;
  max-width: 600px;
  padding: 0 20px;
  text-shadow: 1px 1px 2px rgba(0,0,0,0.3);
`;

const Firework = styled(motion.div)`
  position: absolute;
  width: 4px;
  height: 4px;
  background: ${props => props.color};
  border-radius: 50%;

  &::before {
    content: '';
    position: absolute;
    top: -20px;
    left: -20px;
    width: 40px;
    height: 40px;
    border: 2px solid ${props => props.color};
    border-radius: 50%;
    opacity: 0;
    animation: explode 1s ease-out forwards;
  }

  @keyframes explode {
    0% { transform: scale(0); opacity: 1; }
    100% { transform: scale(3); opacity: 0; }
  }
`;

function NewYearPage({ onComplete }) {
  const [timeLeft, setTimeLeft] = useState({});
  const [isComplete, setIsComplete] = useState(false);
  const [showCelebration, setShowCelebration] = useState(false);
  const [fireworks, setFireworks] = useState([]);

  useEffect(() => {
    const timer = setInterval(() => {
      const now = moment.tz('Asia/Karachi');
      const newYear = moment.tz('2025-01-01 00:00:00', 'Asia/Karachi');
      const diff = newYear.diff(now);

      if (diff > 0) {
        const duration = moment.duration(diff);
        setTimeLeft({
          days: Math.floor(duration.asDays()),
          hours: duration.hours(),
          minutes: duration.minutes(),
          seconds: duration.seconds()
        });
      } else {
        setIsComplete(true);
        setShowCelebration(true);
        clearInterval(timer);

        // Create fireworks
        const newFireworks = Array.from({ length: 15 }, (_, i) => ({
          id: i,
          x: Math.random() * window.innerWidth,
          y: Math.random() * window.innerHeight,
          color: ['#ff6b6b', '#4ecdc4', '#45b7d1', '#96ceb4', '#ffeaa7'][Math.floor(Math.random() * 5)],
          delay: Math.random() * 2
        }));
        setFireworks(newFireworks);

        setTimeout(() => {
          onComplete();
        }, 8000);
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [onComplete]);

  if (showCelebration) {
    return (
      <AnimatePresence>
        <CelebrationOverlay
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <Confetti 
            width={window.innerWidth}
            height={window.innerHeight}
            numberOfPieces={200}
            recycle={false}
          />

          {fireworks.map(firework => (
            <Firework
              key={firework.id}
              color={firework.color}
              style={{ left: firework.x, top: firework.y }}
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ delay: firework.delay, duration: 0.5 }}
            />
          ))}

          <CelebrationText
            initial={{ scale: 0, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ delay: 0.5, duration: 1, type: "spring" }}
          >
            Happy New Year 2025!
          </CelebrationText>

          <CelebrationSubtext
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 1.5, duration: 1 }}
          >
            Here's to new beginnings, Aiman! ðŸŽ‰
          </CelebrationSubtext>
        </CelebrationOverlay>
      </AnimatePresence>
    );
  }

  return (
    <PageContainer>
      <SimpleTitle
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        New Year 2025
      </SimpleTitle>

      <CountdownContainer
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ delay: 0.5, duration: 0.8 }}
      >
        <TimeBox>
          <TimeNumber>{timeLeft.days || 0}</TimeNumber>
          <TimeLabel>Days</TimeLabel>
        </TimeBox>
        <TimeBox>
          <TimeNumber>{timeLeft.hours || 0}</TimeNumber>
          <TimeLabel>Hours</TimeLabel>
        </TimeBox>
        <TimeBox>
          <TimeNumber>{timeLeft.minutes || 0}</TimeNumber>
          <TimeLabel>Minutes</TimeLabel>
        </TimeBox>
        <TimeBox>
          <TimeNumber>{timeLeft.seconds || 0}</TimeNumber>
          <TimeLabel>Seconds</TimeLabel>
        </TimeBox>
      </CountdownContainer>

      <SimpleMessage
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 1, duration: 1 }}
      >
        Counting down to something special...
      </SimpleMessage>
    </PageContainer>
  );
}

export default NewYearPage;
