import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import Confetti from 'react-confetti';

const float = keyframes`
  0% { transform: translateY(0px) rotate(0deg); }
  50% { transform: translateY(-20px) rotate(180deg); }
  100% { transform: translateY(0px) rotate(360deg); }
`;

const bounce = keyframes`
  0%, 20%, 50%, 80%, 100% { transform: translateY(0); }
  40% { transform: translateY(-30px); }
  60% { transform: translateY(-15px); }
`;

const cake = keyframes`
  0% { transform: scale(1) rotate(0deg); }
  25% { transform: scale(1.1) rotate(5deg); }
  50% { transform: scale(1) rotate(0deg); }
  75% { transform: scale(1.1) rotate(-5deg); }
  100% { transform: scale(1) rotate(0deg); }
`;

const CelebrationContainer = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(135deg, #ff9a9e, #fad0c4, #ffd1ff);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const CelebrationText = styled(motion.h1)`
  font-family: 'Dancing Script', cursive;
  font-size: clamp(3rem, 10vw, 5rem);
  font-weight: 700;
  color: #ff6b6b;
  text-align: center;
  margin-bottom: 2rem;
`;

const BirthdayEmoji = styled(motion.div)`
  font-size: clamp(4rem, 15vw, 8rem);
  margin-bottom: 2rem;
  animation: ${cake} 3s ease-in-out infinite;
`;

const CelebrationMessage = styled(motion.p)`
  font-size: clamp(1.2rem, 4vw, 1.8rem);
  color: #666;
  text-align: center;
  max-width: 600px;
  padding: 0 20px;
  margin-bottom: 2rem;
  line-height: 1.6;
`;

const CloseButton = styled(motion.button)`
  background: linear-gradient(45deg, #ff6b6b, #ff8e8e);
  color: white;
  border: none;
  padding: 15px 30px;
  border-radius: 25px;
  font-size: 1.1rem;
  cursor: pointer;
  font-weight: 600;
  box-shadow: 0 5px 15px rgba(255, 107, 107, 0.4);

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(255, 107, 107, 0.6);
  }
`;

const Balloon = styled(motion.div)`
  position: absolute;
  font-size: ${props => props.size}px;
  left: ${props => props.left}%;
  top: ${props => props.top}%;
  z-index: 10;
  animation: ${float} ${props => props.duration}s ease-in-out infinite;
  animation-delay: ${props => props.delay}s;
`;

const BouncingEmoji = styled(motion.div)`
  position: absolute;
  font-size: ${props => props.size}px;
  left: ${props => props.left}%;
  top: ${props => props.top}%;
  animation: ${bounce} ${props => props.duration}s ease-in-out infinite;
  animation-delay: ${props => props.delay}s;
`;

const balloonEmojis = ['üéà', 'üéÄ', 'üåü', 'üíñ', 'üéÅ', 'üå∏'];
const celebrationEmojis = ['üéâ', '‚ú®', 'üíï', 'üå∫', 'ü¶ã', 'üåà'];

function BirthdayCelebration({ onClose }) {
  const [balloons, setBalloons] = useState([]);
  const [celebrations, setCelebrations] = useState([]);

  useEffect(() => {
    // Create balloons
    const newBalloons = Array.from({ length: 12 }, (_, i) => ({
      id: i,
      emoji: balloonEmojis[Math.floor(Math.random() * balloonEmojis.length)],
      left: Math.random() * 80 + 10,
      top: Math.random() * 60 + 20,
      size: Math.random() * 30 + 40,
      duration: Math.random() * 3 + 2,
      delay: Math.random() * 2
    }));

    // Create celebration elements
    const newCelebrations = Array.from({ length: 15 }, (_, i) => ({
      id: i,
      emoji: celebrationEmojis[Math.floor(Math.random() * celebrationEmojis.length)],
      left: Math.random() * 100,
      top: Math.random() * 100,
      size: Math.random() * 25 + 30,
      duration: Math.random() * 2 + 1.5,
      delay: Math.random() * 3
    }));

    setBalloons(newBalloons);
    setCelebrations(newCelebrations);

    // Add more animations every few seconds
    const interval = setInterval(() => {
      const moreBalloons = Array.from({ length: 3 }, (_, i) => ({
        id: Date.now() + i,
        emoji: balloonEmojis[Math.floor(Math.random() * balloonEmojis.length)],
        left: Math.random() * 80 + 10,
        top: Math.random() * 60 + 20,
        size: Math.random() * 30 + 40,
        duration: Math.random() * 3 + 2,
        delay: 0
      }));
      setBalloons(prev => [...prev.slice(-9), ...moreBalloons]);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <AnimatePresence>
      <CelebrationContainer
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1 }}
      >
        <Confetti 
          width={window.innerWidth}
          height={window.innerHeight}
          numberOfPieces={150}
          recycle={true}
        />

        {balloons.map(balloon => (
          <Balloon
            key={balloon.id}
            left={balloon.left}
            top={balloon.top}
            size={balloon.size}
            duration={balloon.duration}
            delay={balloon.delay}
          >
            {balloon.emoji}
          </Balloon>
        ))}

        {celebrations.map(celebration => (
          <BouncingEmoji
            key={celebration.id}
            left={celebration.left}
            top={celebration.top}
            size={celebration.size}
            duration={celebration.duration}
            delay={celebration.delay}
          >
            {celebration.emoji}
          </BouncingEmoji>
        ))}

        <BirthdayEmoji
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ delay: 0.5, type: "spring", stiffness: 200 }}
        >
          üéÇ
        </BirthdayEmoji>

        <CelebrationText
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 1, duration: 1 }}
        >
          Happy Birthday Aiman! üéâ
        </CelebrationText>

        <CelebrationMessage
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 2, duration: 1 }}
        >
          Today is all about celebrating the most amazing person I know! 
          You make every day brighter just by being you! üíñ‚ú®
        </CelebrationMessage>

        <CloseButton
          initial={{ y: 50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ delay: 3, duration: 1 }}
          onClick={onClose}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          Explore Birthday Surprises üéÅ
        </CloseButton>
      </CelebrationContainer>
    </AnimatePresence>
  );
}

export default BirthdayCelebration;
