import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import moment from 'moment-timezone';
import Confetti from 'react-confetti';

const CountdownContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 20px;
  text-align: center;
  position: relative;
  padding-top: 100px;

  @media (max-width: 768px) {
    padding-top: 120px;
  }
`;

const BirthdayTitle = styled(motion.h1)`
  font-family: 'Dancing Script', cursive;
  font-size: clamp(2.5rem, 8vw, 4rem);
  color: white;
  margin-bottom: 1rem;
`;

const PersonalMessage = styled(motion.p)`
  font-size: clamp(1.1rem, 4vw, 1.3rem);
  color: rgba(255, 255, 255, 0.9);
  max-width: 600px;
  line-height: 1.6;
  margin-bottom: 3rem;
`;

const CountdownGrid = styled.div`
  display: flex;
  gap: 1.5rem;
  margin-bottom: 3rem;

  @media (max-width: 768px) {
    gap: 1rem;
  }
`;

const TimeBox = styled(motion.div)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 15px;
  padding: 1.5rem 1rem;
  border: 1px solid rgba(255, 255, 255, 0.2);
  min-width: 80px;

  @media (max-width: 768px) {
    padding: 1rem 0.5rem;
    min-width: 60px;
  }
`;

const TimeNumber = styled.div`
  font-size: clamp(2rem, 6vw, 2.5rem);
  font-weight: 600;
  color: #ffd700;
  line-height: 1;
`;

const TimeLabel = styled.div`
  font-size: 0.9rem;
  color: rgba(255, 255, 255, 0.8);
  margin-top: 0.5rem;
  text-transform: uppercase;
  letter-spacing: 1px;
`;

const SorryNote = styled(motion.div)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 20px;
  padding: 2rem;
  border: 1px solid rgba(255, 255, 255, 0.2);
  max-width: 500px;
  color: rgba(255, 255, 255, 0.9);
  font-style: italic;
  line-height: 1.6;
`;

const CelebrationContainer = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(135deg, #ff9a9e, #fad0c4, #ffd1ff);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const CelebrationText = styled(motion.h1)`
  font-family: 'Dancing Script', cursive;
  font-size: clamp(3rem, 10vw, 5rem);
  font-weight: 700;
  color: #ff6b6b;
  text-align: center;
  margin-bottom: 2rem;
`;

const BirthdayEmoji = styled(motion.div)`
  font-size: clamp(4rem, 15vw, 8rem);
  margin-bottom: 2rem;
`;

const Balloon = styled(motion.div)`
  position: absolute;
  font-size: ${props => props.size}px;
  left: ${props => props.left}%;
  top: ${props => props.top}%;
  z-index: 10;
`;

function BirthdayCountdown() {
  const [timeLeft, setTimeLeft] = useState({});
  const [isComplete, setIsComplete] = useState(false);
  const [showCelebration, setShowCelebration] = useState(false);
  const [balloons, setBalloons] = useState([]);

  useEffect(() => {
    // Check if it's already birthday time
    const now = moment.tz('Asia/Karachi');
    const birthday = moment.tz('2025-01-04 00:00:00', 'Asia/Karachi');

    if (now.isAfter(birthday)) {
      setIsComplete(true);
      setShowCelebration(true);
      return;
    }

    const timer = setInterval(() => {
      const now = moment.tz('Asia/Karachi');
      const birthday = moment.tz('2025-01-04 00:00:00', 'Asia/Karachi');
      const diff = birthday.diff(now);

      if (diff > 0) {
        const duration = moment.duration(diff);
        setTimeLeft({
          days: Math.floor(duration.asDays()),
          hours: duration.hours(),
          minutes: duration.minutes(),
          seconds: duration.seconds()
        });
      } else {
        setIsComplete(true);
        setShowCelebration(true);
        clearInterval(timer);
      }
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    if (showCelebration) {
      // Create floating balloons
      const newBalloons = Array.from({ length: 8 }, (_, i) => ({
        id: i,
        emoji: ['ðŸŽˆ', 'ðŸŽ€', 'ðŸŒŸ', 'ðŸ’–'][Math.floor(Math.random() * 4)],
        left: Math.random() * 80 + 10,
        top: Math.random() * 60 + 20,
        size: Math.random() * 30 + 40,
        delay: Math.random() * 2
      }));
      setBalloons(newBalloons);
    }
  }, [showCelebration]);

  if (showCelebration) {
    return (
      <AnimatePresence>
        <CelebrationContainer
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <Confetti 
            width={window.innerWidth}
            height={window.innerHeight}
            numberOfPieces={150}
          />

          {balloons.map(balloon => (
            <Balloon
              key={balloon.id}
              left={balloon.left}
              top={balloon.top}
              size={balloon.size}
              initial={{ y: 100, opacity: 0 }}
              animate={{ 
                y: [0, -20, 0], 
                opacity: 1,
                rotate: [0, 10, -10, 0]
              }}
              transition={{ 
                delay: balloon.delay,
                duration: 3,
                repeat: Infinity,
                repeatType: "reverse"
              }}
            >
              {balloon.emoji}
            </Balloon>
          ))}

          <BirthdayEmoji
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ delay: 0.5, type: "spring", stiffness: 200 }}
          >
            ðŸŽ‚
          </BirthdayEmoji>

          <CelebrationText
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 1, duration: 1 }}
          >
            Happy Birthday Aiman! ðŸŽ‰
          </CelebrationText>

          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 2, duration: 1 }}
            style={{ 
              fontSize: 'clamp(1.2rem, 4vw, 1.8rem)', 
              textAlign: 'center', 
              maxWidth: '600px', 
              padding: '0 20px',
              color: '#666'
            }}
          >
            Today is all about celebrating you! ðŸ’•
          </motion.p>
        </CelebrationContainer>
      </AnimatePresence>
    );
  }

  return (
    <CountdownContainer>
      <BirthdayTitle
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        Aiman's Birthday! ðŸŽ‰
      </BirthdayTitle>

      <PersonalMessage
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.5, duration: 1 }}
      >
        Hey beautiful! Your special day is almost here. I've been counting down the days 
        because I can't wait to celebrate the most amazing person I know! ðŸ’–
      </PersonalMessage>

      <CountdownGrid>
        <TimeBox
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.8, duration: 0.5 }}
        >
          <TimeNumber>{timeLeft.days || 0}</TimeNumber>
          <TimeLabel>Days</TimeLabel>
        </TimeBox>
        <TimeBox
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 1, duration: 0.5 }}
        >
          <TimeNumber>{timeLeft.hours || 0}</TimeNumber>
          <TimeLabel>Hours</TimeLabel>
        </TimeBox>
        <TimeBox
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 1.2, duration: 0.5 }}
        >
          <TimeNumber>{timeLeft.minutes || 0}</TimeNumber>
          <TimeLabel>Minutes</TimeLabel>
        </TimeBox>
        <TimeBox
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 1.4, duration: 0.5 }}
        >
          <TimeNumber>{timeLeft.seconds || 0}</TimeNumber>
          <TimeLabel>Seconds</TimeLabel>
        </TimeBox>
      </CountdownGrid>

      <SorryNote
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 1.6, duration: 1 }}
      >
        I wish I could be there to celebrate with you in person. I'm sorry I can't give you 
        the biggest hug right now, but know that I'm thinking of you every second and my 
        heart is always with you. You mean everything to me! ðŸ’•
      </SorryNote>
    </CountdownContainer>
  );
}

export default BirthdayCountdown;
