import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import moment from 'moment-timezone';
import Confetti from 'react-confetti';
import Fireworks from './Fireworks';

const sparkle = keyframes`
  0% { opacity: 0; transform: scale(0); }
  50% { opacity: 1; transform: scale(1); }
  100% { opacity: 0; transform: scale(0); }
`;

const glow = keyframes`
  0% { text-shadow: 0 0 20px #ff6b6b; }
  50% { text-shadow: 0 0 30px #ff6b6b, 0 0 40px #ff6b6b; }
  100% { text-shadow: 0 0 20px #ff6b6b; }
`;

const CountdownContainer = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background: linear-gradient(135deg, #1a1a2e, #16213e, #0f3460);
  color: white;
  text-align: center;
  padding: 20px;
  position: relative;
  overflow: hidden;
`;

const Title = styled.h1`
  font-family: 'Dancing Script', cursive;
  font-size: clamp(2rem, 8vw, 4rem);
  font-weight: 700;
  margin-bottom: 1rem;
  animation: ${glow} 2s ease-in-out infinite;
`;

const SubTitle = styled.p`
  font-size: clamp(1rem, 4vw, 1.5rem);
  margin-bottom: 3rem;
  opacity: 0.9;
`;

const CountdownGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 1rem;
  margin-bottom: 3rem;

  @media (max-width: 768px) {
    grid-template-columns: repeat(2, 1fr);
    gap: 0.5rem;
  }
`;

const TimeUnit = styled(motion.div)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 15px;
  padding: 1.5rem 1rem;
  border: 1px solid rgba(255, 255, 255, 0.2);

  @media (max-width: 768px) {
    padding: 1rem 0.5rem;
  }
`;

const TimeNumber = styled.div`
  font-size: clamp(2rem, 6vw, 3rem);
  font-weight: 700;
  color: #ff6b6b;
`;

const TimeLabel = styled.div`
  font-size: clamp(0.8rem, 3vw, 1rem);
  opacity: 0.8;
  margin-top: 0.5rem;
`;

const Message = styled(motion.div)`
  max-width: 600px;
  font-size: clamp(1rem, 4vw, 1.3rem);
  line-height: 1.6;
  margin-bottom: 2rem;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  padding: 2rem;
  border-radius: 20px;
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const CelebrationContainer = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background: linear-gradient(135deg, #ff6b6b, #ff8e8e, #ffa8a8);
  z-index: 1000;
`;

const CelebrationText = styled.h1`
  font-family: 'Dancing Script', cursive;
  font-size: clamp(3rem, 10vw, 6rem);
  font-weight: 700;
  color: white;
  text-align: center;
  margin-bottom: 2rem;
  animation: ${glow} 1s ease-in-out infinite;
`;

const Sparkles = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  pointer-events: none;
`;

const Sparkle = styled.div`
  position: absolute;
  width: 4px;
  height: 4px;
  background: white;
  border-radius: 50%;
  animation: ${sparkle} 2s ease-in-out infinite;
  animation-delay: ${props => props.delay}s;
  left: ${props => props.left}%;
  top: ${props => props.top}%;
`;

function NewYearCountdown({ onComplete }) {
  const [timeLeft, setTimeLeft] = useState({});
  const [isComplete, setIsComplete] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => {
      const now = moment.tz('Asia/Karachi');
      const newYear = moment.tz('2025-01-01 00:00:00', 'Asia/Karachi');
      const diff = newYear.diff(now);

      if (diff > 0) {
        const duration = moment.duration(diff);
        setTimeLeft({
          days: Math.floor(duration.asDays()),
          hours: duration.hours(),
          minutes: duration.minutes(),
          seconds: duration.seconds()
        });
      } else {
        setIsComplete(true);
        setShowConfetti(true);
        clearInterval(timer);
        onComplete();
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [onComplete]);

  const sparkleElements = Array.from({ length: 50 }, (_, i) => (
    <Sparkle
      key={i}
      delay={Math.random() * 2}
      left={Math.random() * 100}
      top={Math.random() * 100}
    />
  ));

  if (isComplete) {
    return (
      <AnimatePresence>
        <CelebrationContainer
          initial={{ opacity: 0, scale: 0.5 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1 }}
        >
          {showConfetti && <Confetti />}
          <Fireworks />
          <CelebrationText>
            Happy New Year 2025!
            <br />
            My Beautiful Aiman! ðŸŽ‰
          </CelebrationText>
          <motion.p
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 1, duration: 1 }}
            style={{ fontSize: 'clamp(1.2rem, 5vw, 2rem)', textAlign: 'center', maxWidth: '600px', padding: '0 20px' }}
          >
            Another year begins, and I'm so grateful to have you in my life. 
            Here's to making 2025 even more beautiful together! ðŸ’–
          </motion.p>
        </CelebrationContainer>
      </AnimatePresence>
    );
  }

  return (
    <CountdownContainer>
      <Sparkles>{sparkleElements}</Sparkles>

      <motion.div
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        <Title>New Year 2025 Countdown</Title>
        <SubTitle>For my amazing Aiman Rabbani ðŸ’–</SubTitle>
      </motion.div>

      <CountdownGrid>
        <TimeUnit
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.2, duration: 0.5 }}
        >
          <TimeNumber>{timeLeft.days || 0}</TimeNumber>
          <TimeLabel>Days</TimeLabel>
        </TimeUnit>
        <TimeUnit
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.4, duration: 0.5 }}
        >
          <TimeNumber>{timeLeft.hours || 0}</TimeNumber>
          <TimeLabel>Hours</TimeLabel>
        </TimeUnit>
        <TimeUnit
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.6, duration: 0.5 }}
        >
          <TimeNumber>{timeLeft.minutes || 0}</TimeNumber>
          <TimeLabel>Minutes</TimeLabel>
        </TimeUnit>
        <TimeUnit
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.8, duration: 0.5 }}
        >
          <TimeNumber>{timeLeft.seconds || 0}</TimeNumber>
          <TimeLabel>Seconds</TimeLabel>
        </TimeUnit>
      </CountdownGrid>

      <Message
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 1, duration: 1 }}
      >
        My dearest Aiman, as we countdown to 2025, I want you to know how much you mean to me. 
        Every moment with you is a celebration, and I can't wait to create more beautiful memories 
        together in the new year. You light up my world like fireworks in the sky! âœ¨
      </Message>
    </CountdownContainer>
  );
}

export default NewYearCountdown;
