import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import NewYearPage from './NewYearPage';
import BirthdayCountdown from './BirthdayCountdown';
import BirthdayGallery from './BirthdayGallery';
import InteractiveGames from './InteractiveGames';

const TestContainer = styled.div`
  min-height: 100vh;
  position: relative;
`;

const TestNavigation = styled.div`
  position: fixed;
  top: 20px;
  left: 20px;
  z-index: 1000;
  display: flex;
  flex-direction: column;
  gap: 10px;
  background: rgba(0, 0, 0, 0.8);
  backdrop-filter: blur(10px);
  padding: 20px;
  border-radius: 15px;
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const TestButton = styled.button`
  background: ${props => props.active ? '#ff6b6b' : 'rgba(255, 255, 255, 0.1)'};
  color: white;
  border: none;
  padding: 10px 15px;
  border-radius: 8px;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.3s ease;
  min-width: 150px;

  &:hover {
    background: ${props => props.active ? '#ff6b6b' : 'rgba(255, 255, 255, 0.2)'};
    transform: translateY(-1px);
  }
`;

const TestTitle = styled.h3`
  color: white;
  margin: 0 0 10px 0;
  font-size: 1rem;
  text-align: center;
`;

const CloseTestMode = styled.button`
  background: #ff4757;
  color: white;
  border: none;
  padding: 8px 12px;
  border-radius: 6px;
  font-size: 0.8rem;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background: #ff3742;
  }
`;

function TestMode({ onClose }) {
  const [currentPage, setCurrentPage] = useState('newYear');

  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'newYear':
        return <NewYearPage onComplete={() => console.log('New Year Complete!')} />;
      case 'birthdayCountdown':
        return <BirthdayCountdown />;
      case 'birthdayGallery':
        return <BirthdayGallery />;
      case 'games':
        return <InteractiveGames />;
      default:
        return <NewYearPage onComplete={() => console.log('New Year Complete!')} />;
    }
  };

  return (
    <TestContainer>
      <TestNavigation>
        <TestTitle>üß™ Test Mode</TestTitle>

        <TestButton 
          active={currentPage === 'newYear'} 
          onClick={() => setCurrentPage('newYear')}
        >
          üéä New Year Page
        </TestButton>

        <TestButton 
          active={currentPage === 'birthdayCountdown'} 
          onClick={() => setCurrentPage('birthdayCountdown')}
        >
          üéÇ Birthday Countdown
        </TestButton>

        <TestButton 
          active={currentPage === 'birthdayGallery'} 
          onClick={() => setCurrentPage('birthdayGallery')}
        >
          üíï Birthday Gallery
        </TestButton>

        <TestButton 
          active={currentPage === 'games'} 
          onClick={() => setCurrentPage('games')}
        >
          üéÆ Interactive Games
        </TestButton>

        <CloseTestMode onClick={onClose}>
          ‚ùå Exit Test Mode
        </CloseTestMode>
      </TestNavigation>

      <AnimatePresence mode="wait">
        <motion.div
          key={currentPage}
          initial={{ opacity: 0, x: 100 }}
          animate={{ opacity: 1, x: 0 }}
          exit={{ opacity: 0, x: -100 }}
          transition={{ duration: 0.3 }}
        >
          {renderCurrentPage()}
        </motion.div>
      </AnimatePresence>
    </TestContainer>
  );
}

export default TestMode;
